mapper.select:
SELECT  COUNT(*)
FROM    tb_question

    WHERE question_type_sid = #{questionType}

SELECT TQ.*, QT.type_name,TU.user_name FROM tb_question AS TQ LEFT OUTER JOIN tb_question_type AS QT ON TQ.question_type_sid = QT.question_type_sid LEFT OUTER JOIN tb_user AS TU ON TQ.user_sid = TU.user_sid
WHERE TQ.question_type_sid = #{questionType}
ORDER BY TQ.question_sid DESC LIMIT #{recordSize} offset #{pagination.limitStart}
