mapper.select:
SELECT COUNT(*) FROM tb_reservation WHERE reserve_status = '예약완료' AND user_sid = #{user_sid}
AND ((CURRENT_TIMESTAMP between start_date AND end_date) OR (start_date > CURRENT_TIMESTAMP))
AND CURRENT_TIMESTAMP > end_date

SELECT RES.*,TU.user_name,RT.type_name AS room_name FROM tb_reservation AS RES LEFT OUTER JOIN tb_user AS TU ON RES.user_sid = TU.user_sid LEFT OUTER JOIN tb_room AS TR ON RES.room_sid = TR.room_sid LEFT OUTER JOIN tb_room_type AS RT ON TR.room_type_sid = RT.room_type_sid WHERE RES.reserve_status = '예약완료' AND RES.user_sid = #{user_sid}
AND ((CURRENT_TIMESTAMP between RES.start_date AND RES.end_date) OR (RES.start_date > CURRENT_TIMESTAMP))
AND CURRENT_TIMESTAMP > RES.end_date
ORDER BY RES.reserve_sid DESC LIMIT #{recordSize} offset #{pagination.limitStart}

mapper.update:
UPDATE 	tb_reservation
SET		reserve_status = #{reserveStatus}
WHERE
    	reserve_sid = #{reserveSid}
