mapper.insert:
INSERT INTO tb_options(
	room_sid,
	option_name,
	option_value
) VALUES (
	#{room_sid},
	#{option_name},
	#{option_value}
)

mapper.delete:
UPDATE tb_room
SET	delete_yn = 'Y'
WHERE	room_sid = #{room_sid}

mapper.update:
UPDATE tb_room
SET delete_yn = 'N'
WHERE room_sid = #{room_sid}

UPDATE tb_options
SET	option_name = {option_name},
	option_value = #{option_value}
WHERE	room_sid = #{room_sid}
