mapper.delete:
UPDATE tb_room
SET	delete_yn = 'Y'
WHERE	room_sid = #{room_sid}

mapper.update:
UPDATE tb_room
SET delete_yn = 'N'
WHERE room_sid = #{room_sid}

UPDATE tb_options
SET	option_name = #{option_name},
	option_value = #{option_value},
	option_type=#{option_type}
WHERE	room_sid = #{room_sid}
