mapper.insert:
INSERT INTO tb_room_type(
    type_name,
    room_size,
    bed_size
)   VALUES  (
    #{type_name},
    #{room_size},
    #{bed_size}
)

mapper.update:
UPDATE  tb_room_type
SET     type_name = #{type_name},
        room_size = #{room_size},
        bed_size = #{bed_size}
WHERE
        room_type_sid = #{room_type_sid}

mapper.delete:
DELETE
FROM    tb_type_img
WHERE
        img_name = #{current_img}
  AND   room_type_sid = #{room_type_sid}
